--
-- 表的结构 `ab_company`
--

CREATE TABLE IF NOT EXISTS `ab_company` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL COMMENT '公司名',
  `address` varchar(100) NOT NULL COMMENT '公司地址',
  `desc` varchar(500) NOT NULL,
  `mobile` varchar(11) NOT NULL,
  `fex` varchar(50) NOT NULL,
  `culture` mediumtext NOT NULL COMMENT '公司文化',
  `history_tit` mediumtext NOT NULL COMMENT '历史回顾title',
  `history_desc` mediumtext NOT NULL,
  `greetings` mediumtext NOT NULL COMMENT '????',
  `service` mediumtext NOT NULL COMMENT '????',
  `compliant` mediumtext NOT NULL COMMENT '????',
  `tel` varchar(50) NOT NULL COMMENT '????',
  `email` varchar(50) NOT NULL COMMENT '??',
  `copyright` varchar(500) NOT NULL COMMENT '????',
  `set_keys` text,
  `set_desc` text COMMENT '网站desc',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='?????' AUTO_INCREMENT=3 ;

--
-- 转存表中的数据 `ab_company`
--

INSERT INTO `ab_company` (`id`, `name`, `address`, `desc`, `mobile`, `fex`, `culture`, `history_tit`, `history_desc`, `greetings`, `service`, `compliant`, `tel`, `email`, `copyright`, `set_keys`, `set_desc`) VALUES
(1, 'Auburn Systems，LLC. 奥本科技', '北京市朝阳区东三环北路三号幸福大厦B座1617A', '美国奥本公司总部设在美国东北部城市波士顿世界著名高科技工业城市，是静电测量技术的发明企业，拥有合法专利权．是管道粉尘泄漏监测技术的领航者．', '13671348898', '+86-010-64619688', '', '奥本，颗粒物排放监测的行业标准', '奥本系统公司（奥本）是一家在高技术工业仪器领域的创新型公司。奥本是微电荷（triboelectric）粉尘监测技术的发明者，并且在粉尘排放监测设备的制造领域具有领先地位。<br>从1970年代开始，已有400000多套奥本微电荷（triboelectric）粉尘监测设备安装在世界各地的12000多家物料处理工厂中，其中大部分还在正常运行当中。这些工厂包括：钢铁，水泥，火电，冶金，铸造，食品加工，制药，及其它更广泛的领域。奥本的产品不仅满足了粉尘排放泄漏检测的需要，而且也被广泛地应用到了颗粒状物料流动的过程控制当中。', '', '', '<p class="wenben">Auburn ''s OPC server   software package allows Auburn TRIBO.<em>series</em>, dust monitors to exchange   real-time and historical data effortlessly with HMI/SCADA software and   PLCs.This open connectivity standard,   adopted by more than 60% of North American process industries, is spreading   rapidly around the world, providing TRIBO.<em>series</em> monitoring equipment and data   handling systems widespread usability with a diverse and comprehensive group of   process industries utilizing particulate monitors.</p><a href="http://www.opcfoundation.org/" target="_blank" class="lianj">Click here for the OPC Foundation Webpage</a><table width="100%" border="0" cellpadding="5" cellspacing="15">              <tbody>              <tr>                <td align="center" valign="top"><img src="http://www.auburnsys.cn/imges/2.jpg" width="141" height="183" alt="" /></td>                <td align="center" valign="top"><img src="imges/3.jpg" width="140" height="184" alt="" /></td>                <td align="center" valign="top"><img src="imges/4.jpg" width="143" height="187" alt="" /></td>              </tr>              <tr>                <td align="center" valign="middle"><img src="imges/5.jpg" width="189" height="251" alt="" /></td>                <td colspan="2" align="center" valign="middle" class="wenben-cn">美国EPA对比结果<br />                  <br />                  <table width="100%" border="1" cellpadding="5" cellspacing="0">                  <tbody><tr>                    <td align="center" class="wenben-cn">对比项目</td>                    <td align="center">奥本系统</td>                    <td align="center">光学探测</td>                  </tr>                  <tr>                    <td align="center">袋漏侦测</td>                    <td align="center">一小时内</td>                    <td align="center">两天</td>                  </tr>                  <tr>                    <td align="center">袋漏定位</td>                    <td align="center">一分钟内</td>                    <td align="center">2-3人若干小时</td>                  </tr>                  <tr>                    <td align="center">清洁耗时</td>                    <td align="center">一人一小内</td>                    <td align="center">8-10人若干小时</td>                  </tr>                  <tr>                    <td align="center">清洁用工具</td>                    <td align="center">普通吸尘器</td>                    <td align="center">铁铲</td>                  </tr>                </tbody></table></td>                </tr>            </tbody></table>', '+86-010-64678088', 'sales@auburnsys.cn', 'Copyright©2005-2014 auburnsys.CN All Rights Reserved 奥本科技', NULL, NULL);

