--
-- 表的结构 `ab_issue`
--

CREATE TABLE IF NOT EXISTS `ab_issue` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(500) NOT NULL,
  `content` mediumtext NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=24 ;

--
-- 转存表中的数据 `ab_issue`
--

INSERT INTO `ab_issue` (`id`, `title`, `content`) VALUES
(1, '仪器是否可以检测对机床，厂房的整体和个体的粉尘浓度的测定？', '微电荷粉尘监测仪可对在各种形式的管道内定向流动的气流中所夹带的颗粒物进行测量。对于开放空间（如机床周围或厂房整体空间）内的颗粒物，可在通风换气管道吸气口内安装微电荷粉尘监测仪，或利用文丘里管式探头来进行测量。'),
(2, '是否有“粉尘浓度”的确切定义？如果有，是如何定义的？', '粉尘浓度是指分布在单位体积的空间内固体颗粒物的总重量，以mg/m^3 或 g/m^3 表示。'),
(3, '仪器的检测原理？', '当粉尘颗粒撞击到探头时就会产生电荷转移，这就是通常所说得的摩擦起电，转移电荷通过探头流向接地点，这就是转移电流；分布在空间中的颗粒物，由于各种原因，都带有一定电荷，当带电荷的颗粒物从探头附近飞过，电场的变化就会在探头上产生感应电流。粉尘颗粒微电荷探测设备通过测量由探头流向接地点的微弱的转移电流和感应电流，并处理这个电流信号，来检测粉尘的流量和浓度。'),
(4, '与称重法的关系和比较？', '称重法是测量粉尘浓度的直接方法。根据粉尘浓度的定义，在一定时间内用截留在滤膜上粉尘重量除以在同一时间内流过滤膜的气体的体积，即得到粉尘浓度。除称重法以外的所有粉尘浓度的测量方法都是间接方法。称重法虽然反映了粉尘浓度的定义，但其操作是离线式的，间断性的，并会由于重复的手工操作而产生误差。微电荷粉尘监测仪可对粉尘浓度和流量进行自动的，实时在线的连续测量，并且易于维护，不需要滤膜等耗材。'),
(5, 'DGD中有4个功能卡插槽，但功能卡却有5种。请问，如果要装满4个插槽，哪一个功能卡应该舍去，而舍弃的该功能卡的功能还能否通过其他途径在DGD系统中发挥作用？', ' 功能卡不止5种，至于要舍弃哪个功能卡，是要根据用户对各种功能的需求（用户不一定需要这种功能）。当然舍弃哪个功能卡，系统就无法发挥该功能卡的作用。<br>但有些功能卡的作用可以在软件中进行实现，比如报警卡（通过软件设定信号的报警值达到报警的功能）。'),
(6, '请解释DGD中，探头所输出的RS485数字信号？', '详细的回答见问题3（为扩展应用范围，EIA又于1983年在RS-422基础上制定了RS-485标准，增加了多点、双向通信能力，即允许多个发送器连接到同一条总线上，同时增加了发送器的驱动能力和冲突保护特性，扩展了总线共模范围，后命名为TIA/EIA-485-A标准。）。<br> 485是半双工，２线。'),
(7, 'Tribo.3400及Tribo.dgd如何在电除尘系统中发挥作用，以达到节能降耗，提高除尘率的目的？', '回答1： Tribo.link在国内暂不销售，所以就不做回答了。  <br>\r\n回答2： Tribo.dgd主要是功能是监测粉尘的浓度，并且它的监测能力非常高。针对该问题我们做如下分析： <br>\r\n（1）静电除尘器的工作原理 <br>\r\n静电除尘器的工作原理是利用高压电场使烟气发生电离，气流中的粉尘荷电在电场作用下与气流分离。负极由不同断面形状的金属导线制成，叫放电电极。正极由不同几何形状的金属板制成，叫集尘电极。 <br>\r\n静电除尘器的性能受粉尘性质、设备构造和烟气流速等三个因素的影响。粉尘的比电阻是评价导电性的指标，它对除尘效率有直接的影响。比电阻过低，尘粒难以保持在集尘电极上，致使其重返气流。比电阻过高，到达集尘电极的尘粒电荷不易放出，在尘层之间形成电压梯度会产生局部击穿和放电现象。这些情况都会造成除尘效率下降。 <br>\r\n静电除尘器的电源由控制箱、升压变压器和整流器组成。电源输出的电压高低对除尘效率也有很大影响。因此，静电除尘器运行电压需保持40一75kV乃至100kV以上。 <br>\r\n（2）Tribo.dgd在静电除尘器的应用及其效果 Tribo.<br>dgd主要是能向静电除尘器的控制系统提供反应粉尘浓度变化的的信号，从而控制系统可以根据Tribo.<br>dgd提供的信号调整输出的电压高低，达到节能降耗的目的。 <br>\r\n（3）Tribo.dgd在静电除尘器的应用的优点。 <br>\r\n当电除尘系统发生泄露时，由于使用精确的Tribo.dgd监测系统，在粉尘发生微小的泄露时（肉眼无法观测到），就能监测到泄露。从而提前对设备进行维护，避免泄露进一步严重化。 <br>\r\n其次，提前发现泄露，这样对于维护人员有充足的时间对设备进行维护。这样会可能避免停厂的经济损失。<br>  \r\n再则，Tribo.dgd监测系统维护费用非常低。<br>\r\nTribo.dgd运行稳定。几乎是零维护；而使用其他设备时，将花费的高额维护费用。'),
(8, ' 316不锈钢探头本就具有耐高温的性能，为何还要设定管内气流温度？管内气流温度对探头有何影响？', '气流的温度与信号的强弱没有直接关系，只要温度保持在冷凝水平上，高于最低温度限制即可。如果降到凝点以下，水珠将会粉尘信号基础上产生额外的信号。另外，探棒上液态的水会在绝缘体上形成架桥效果。 <br>\r\n但是当温度达到593℃以上时，不管是什么物质，都会产生自由电子，干扰正常的信号。这种现象随温度升高而加强。设备内置温度补偿机制。探棒产生的电流十分微弱，以致探棒或信号线缆的电阻变化小得几乎没有意义。 <br>\r\n所以，当温度高于一定值时潮湿的环境中，要用特殊性能的探棒。'),
(9, 'Tribo.4002、Tribo.3400有何区别，请就两者的优劣详细阐明？', 'Tribo.3400是Tribo.4002的升级版。Tribo.3400可看作Tribo.4002联体式的高度集成。 <br>\r\nTribo.3400使用24 VDC （直流）“回路供电”，无须额外的电源。可通过TRIBO.<br>dgd监测系统供电，或直接接入PLC由PLC供电。Tribo.4002要额外的电源。<br>\r\nTribo.3400结合了TRIBO.diditek，TRIBO.diditek <br>是一个32位，浮点运算方法的处理程序，代表了在现有工业中运用的最新的静电处理电路。这个集成在 TRIBO.diditek 里面的独特处理方法是专门用于处理高信号的，来提供一个大的数据范围和多样的系统功能；根据用户选择，可选的输出信号范围为8个数据片段或是线性的。它是基于DC直流，采集最突出的摩擦静电信号，并结合有AC交流信号统计——可进行适当调整，这样当使用有涂抹层的探棒时无需进行电路转换。'),
(10, '袋漏卡16trac/32trac是不是指16排/32排，如不是，是指什么？DGD、4002、TRIBO.LINK及SENTRY都自带报警功能吗？如不是，那是否是安装了报警卡？', '是指16排/32排。 <br> DGD、4002可在软件中设定报警功能或接入PLC，通过PLC的程序进行报警。而SENTRY是老产品，已停产不销售了。'),
(11, '称重法中使用的“滤膜”是否关标准，如果有，希望能提供标准号。选择滤膜的依据是什么？', '微电荷粉尘监测仪不需要滤膜等耗材。'),
(12, 'DGD监测软件、DGD全功能软件、袋漏监测软件有何区别，请阐明其各自的使用条件、适用范围和优劣对比情况？而安装了袋漏监测软件的系统是否就不需安装袋漏卡了？', 'DGD监测软件，是配合Tribo.dgd使用的，即可以在PC上显示实时监测数据以及图形；DGD全功能软件是比DGD监测软件功能更健全的版本。袋漏监测软件是一个监测软件，使用该软件就不需要硬件的袋漏监测卡了。'),
(13, '316号探针、高碳精探针、防水探针有何功能区别，如何根据情况选用适当的探针？', '是316号探针，也就是不锈钢探棒，用于一般的工况条件（非潮湿）。<br>\r\n   高碳精探针，耐高温性能，用于用于高温度的工况条件。（非潮湿） 防水探针，可防水性能，用于潮湿或有化学腐蚀的工况条件。'),
(14, '你公司所提供的以太网网卡与市场中的网卡有何区别，是否可以替代？专用线有何作用，是否有替代品？', '我公司提供的网卡是工业用的网卡，而市场用的网卡是家用PC的网卡。<br>\r\n   专用线有屏蔽干扰信号的功能，有替代产品。'),
(15, '探头的陶瓷绝缘配件的价格？', '没有单独的配件，它是一个整体的探棒；也就是陶瓷探棒或称防水探棒，是一种特殊的探棒。\r\n<br>价格见防水探棒的价格。'),
(16, 'DGD中单通道和双通道有何区别？', '单通道的DGD，只能接入1个探棒信号，这样就无法实现测量粉尘流速的功能（流速和浓度不一样）；<br>\r\n   双通道的DGD，可2个探棒信号，这样就能实现测量粉尘流速的功能（流速和浓度不一样）。'),
(17, '输入卡和输出卡所起的作用能在DGD或4002中通过设定来实现吗，或者说能通过所插的软件来实现?', '（1）ＤＧＤ中： 输入卡，主要作用就是把工业系统中其他的4～２０mA信号接到ＤＧＤ中，这样可以以信号曲线的方式显示在ＤＧＤ的屏幕上，所以就无须通过软件来实现即可。 <br>\r\n但是软件中，也可同时实现信号曲线的显示。<br> \r\n输出卡,主要作用就是把DGD检测到的信号以4～２０mA的模拟信号方式,提供给工业系统。<br>\r\n（2）4002中： <br>输入卡，输出卡只在DGD中使用，4002中不使用输入卡和输出卡；4002直接提供屏幕柱状显示和4～２０mA的模拟信号输出。'),
(18, '是否有“粉尘浓度的测定”标准？', '在不同的领域，粉尘浓度的测定有不同的标准。在粉尘排放在线监测、除尘器泄漏监测等低浓度（稀相）粉尘浓度测量领域，微电荷粉尘监测仪符合美国环保署的标准。实际上，奥本公司参与了其中一些标准的制定。'),
(19, 'DGD系统或4002系统所监测的袋漏，是将监测定位控制在排的范围上还是具体哪个袋点上？如不能定位在具体的袋点上，要加装什么才能达到这种要求？价格是多少？', '首先，DGD或4002本身不能提供袋漏监测定位的功能，它们只提供检测粉尘浓度或流速的功能。<br> \r\n它们是结合TRIBO.TRAC袋漏监测卡组成一个系统时，才能有袋漏定位的功能 。 <br>\r\n这个问题其实可归结为：用户要检测多少个点？ <br>\r\n（1）比如，用户有3600个布袋，如果要监测到每一个布袋，就需要监测3600个点。 <br>\r\n（2）如果用户只要监测到排，则只要监测100点即可（假设1个排有36个布袋）。 <br>\r\n所以我们是根据点数保价，一般如果用户有很多的布袋（大型除尘系统），我们建议只要监测到排即可（当然我们也能作到监测到排），但价格可能会直线上升。 <br>\r\n对于小型的布袋除尘系统，结合实际情况可监测到每一个布袋。<br>\r\n另外，我们现在最新的软件定位有更高的性价比。监测点大于32点以上建议使用 。'),
(20, 'DGD、4002等监测系统能否对排放的气体进行检测、监控或起其他什么作用？能不能达到对CO2、CO、SO2、SO3、NO等有害气体的控制排放或其他什么作用？', '功能卡不止5种，至于要舍弃哪个功能卡，是要根据用户对各种功能的需求（用户不一定需要这种功能）。当然舍弃哪个功能卡，系统就无法发挥该功能卡的作用。<br>\r\n    但有些功能卡的作用可以在软件中进行实现，比如报警卡（通过软件设定信号的报警值达到报警的功能）。'),
(21, '我们的产品可以测油雾浓度吗？', '我们的产品可以测油雾浓度。由于油滴会附着在探头表面，新的油滴颗粒撞击探头时不会产生转移电流，但我们可以通过测量感应电流的方法来检测粉尘的流量和浓度。在这种情况下，我们会在普通的金属探头外镀一层特弗龙膜，以保持信号的一致性和便于清理。'),
(22, ' 请解释DGD中，探头所输出的RS485数字信号？', '在一定条件下，微电荷粉尘监测仪输出的信号与称重法测得的标准浓度和流量呈线性关系。这个线性关系已被理论和大量的实践证明，例如在重庆煤炭科学研究院的测试表明，在煤粉流速为大约20m/s的条件下，微电荷粉尘监测仪输出的信号与浓度的线性相关系数为0.99。\r\n<table border="1" cellspacing="0" cellpadding="0" width="495">\r\n<tbody><tr>\r\n<td width="373" nowrap="" colspan="3" valign="bottom"><p><strong>2008-01-25, Chongqing Coal Research Institute</strong></p></td>\r\n<td width="122"><p><strong>&nbsp;</strong></p></td>\r\n</tr>\r\n<tr>\r\n<td width="373" nowrap="" colspan="3" valign="bottom"><p>Test    conducted on Venturi Sensor, air velocity at 20m/s</p></td>\r\n<td width="122"><p>&nbsp;</p></td>\r\n</tr>\r\n<tr>\r\n<td width="94"><p>&nbsp;</p></td>\r\n<td width="94"><p>&nbsp;</p></td>\r\n<td width="185"><p>&nbsp;</p></td>\r\n<td width="122"><p>&nbsp;</p></td>\r\n</tr>\r\n<tr>\r\n<td width="94"><p>Data    Group</p></td>\r\n<td width="94"><p>Time</p></td>\r\n<td width="185"><p>Concentration    (mg/m3)</p></td>\r\n<td width="122"><p>Tribo    (pA)</p></td>\r\n</tr>\r\n<tr>\r\n<td width="94"><p>1</p></td>\r\n<td width="94"><p>22:42:00</p></td>\r\n<td width="185"><p>19.5</p></td>\r\n<td width="122"><p>7.082742719</p></td>\r\n</tr>\r\n<tr>\r\n<td width="94"><p>2</p></td>\r\n<td width="94"><p>22:51:30</p></td>\r\n<td width="185"><p>18.7</p></td>\r\n<td width="122"><p>7.482235085</p></td>\r\n</tr>\r\n<tr>\r\n<td width="94"><p>3</p></td>\r\n<td width="94"><p>22:59:10</p></td>\r\n<td width="185"><p>94.7</p></td>\r\n<td width="122"><p>32.02358679</p></td>\r\n</tr>\r\n<tr>\r\n<td width="94"><p>4</p></td>\r\n<td width="94"><p>23:05:15</p></td>\r\n<td width="185"><p>151.9</p></td>\r\n<td width="122"><p>61.55670102</p></td>\r\n</tr>\r\n<tr>\r\n<td width="94"><p>5</p></td>\r\n<td width="94"><p>23:11:40</p></td>\r\n<td width="185"><p>178.5</p></td>\r\n<td width="122"><p>75.11393807</p></td>\r\n</tr>\r\n<tr>\r\n<td width="94"><p>6</p></td>\r\n<td width="94"><p>23:19:00</p></td>\r\n<td width="185"><p>235.6</p></td>\r\n<td width="122"><p>99.29928173</p></td>\r\n</tr>\r\n<tr>\r\n<td width="94"><p>7</p></td>\r\n<td width="94"><p>23:25:00</p></td>\r\n<td width="185"><p>105.7</p></td>\r\n<td width="122"><p>43.33431498</p></td>\r\n</tr>\r\n</tbody></table>\r\n除了浓度或流量以外，微电荷粉尘监测仪输出的信号还与颗粒物流速、探头的形状、位置、颗粒物大小、材质及介电常数等性质有关。所以，微电荷粉尘监测是一种相对的测量方法，在一定流速范围内，测量条件基本稳定的条件下，需要通过标定来确定微电荷粉尘监测仪输出的信号与粉尘浓度或流量的线性关系。奥本开发了一系列的数字信号处理和统计算法来一定范围内消除流速的影响，但是一种颗粒物在特定条件下的标定数据不应被用在其他场合。\r\n<ul type="circle">\r\n<li>微电荷粉尘监测仪的标定通常要在现场进行。</li>\r\n<li>要保证微电荷粉尘监测仪的探测位置与采样位置尽可能地接近。</li>\r\n<li>在一定时间段内，保持浓度或流量数值尽量稳定，在采集微电荷粉尘监测仪数据的同时，用滤纸收集粉尘，纪录同一时间内粉尘重量以计算份浓度或流量。</li>\r\n<li>然后变化粉尘的浓度或流量，重复上一步骤。在要测量的浓度或流量的范围内取若干点，应尽可能地控制自变量数值均匀分布。</li>\r\n<li>用不同时间段内微电荷粉尘监测仪数据的平均值和相应的称重法数据进行拟合，得到微电荷粉尘监测仪信号与浓度或流量的线性关系。</li>\r\n</ul>\r\n要保证每一时间段有足够的采样时间，尤其是在浓度较低的条件下，以保证称重法的精度。'),
(23, 'DGD、4002等产品如何在水除尘系统设备中发挥作用？', '如果是监测最后排放到空气中的粉尘浓度，很简单，就是在末端排气烟囱上安装我们的设备即可。');

